package variablesAndDatatypes;

public class Concatination {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		String c="Gauri";
				System.out.println(a+b+c);
		System.out.println(a+c+b);
		System.out.println(c+a+b);
		System.out.println(c+(a+b));

	}

}
